void dateboardViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &closebt)
    {
        //close
        //When closebt clicked change screen to mainboard
        //Go to mainboard with no screen transition
        application().gotomainboardScreenNoTransition();
    }
    else if (&src == &minusbt)
    {
        //clear
        //When minusbt clicked show clearbox
        //Show clearbox
        clearbox.setVisible(true);
        clearbox.invalidate();
    }
    else if (&src == &yesbt)
    {
        //yesbtclear
        //When yesbt clicked hide clearbox
        //Hide clearbox
        clearbox.setVisible(false);
        clearbox.invalidate();

        //cleartable
        //When yesbt clicked call virtual function
        //Call ClearTable
        ClearTable();
    }
    else if (&src == &nobt)
    {
        //nobt
        //When nobt clicked hide clearbox
        //Hide clearbox
        clearbox.setVisible(false);
        clearbox.invalidate();
    }
}
